#!/bin/bash
echo "                                                                                                 ";
echo " _|                    _|   _|                              _|                               _|  ";
echo " _|  _|       _|_|_|   _|          _|_|_|          _|_|_|   _|     _|_|     _|    _|     _|_|_|  ";
