echo "                                                                                                 ";
echo "                                                                                                 ";
echo "                                                                                                 ";
echo "                                                                                                 ";
echo "                                                                                                 ";
echo "                                                                                                 ";
echo "                                                                                                 ";
echo " Egg for MCGalaxyCli for Pterodactyl.                                                            ";
echo "                                                                                                 ";
echo "                                                                                                 ";
sleep 15
mkdir /mnt/server
cd /mnt/server 
apt update 
apt -y upgrade
echo "Starting installation..."
apt -y install wget unzip mono-runtime
LATEST_RELEASE=$(wget -qO- https://api.github.com/repos/UnknownShadow200/MCGalaxy/releases/latest | grep browser_download_url | cut -d '"' -f 4)
wget $LATEST_RELEASE -O MCGalaxy.zip
unzip -j MCGalaxy.zip
echo "Done extracting..."
chmod +X *.dll
chmod +X *.exe
echo "Done"
echo "Hey check out https://kalia.cloud/ for cheap ClassiCube hosting! " > /text/welcome.txt
exit 0
